  `,
  styles: [`
    .hero-section {
      height: 100vh;
      min-height: 600px;
      background-image: url('assets/images/bangalore-hero.jpg');
      background-size: cover;
      background-position: center;
      position: relative;
    }
    
    .hero-overlay {
      position: absolute;
      top: 0;
      left: 0;
      width: 100%;
      height: 100%;
      background: linear-gradient(rgba(0,0,0,0.5), rgba(0,0,0,0.7));
    }
    
    .text-highlight {
      color: #2989d8;
      position: relative;
    }
    
    .text-highlight:after {
      content: '';
      position: absolute;
      width: 100%;
      height: 4px;
      background-color: #2989d8;
      bottom: -5px;
      left: 0;
    }
    
    .search-box {
      max-width: 600px;
    }
    
    .search-box .form-control {
      height: 54px;
      border-radius: 27px 0 0 27px;
      border: none;
      box-shadow: 0 5px 15px rgba(0,0,0,0.1);
    }
    
    .search-box .btn {
      border-radius: 0 27px 27px 0;
      height: 54px;
      padding-left: 25px;
      padding-right: 25px;
      background: linear-gradient(135deg, #1e5799 0%,#207cca 51%,#2989d8 100%);
      border: none;
    }
    
    .hero-categories {
      display: flex;
      flex-wrap: wrap;
      gap: 15px;
    }
    
    .hero-category-item {
      display: flex;
      flex-direction: column;
      align-items: center;
      color: white;
      text-decoration: none;
      transition: all 0.3s;
      padding: 15px;
      border-radius: 10px;
      background-color: rgba(255,255,255,0.1);
      backdrop-filter: blur(10px);
      width: 100px;
    }
    
    .hero-category-item i {
      font-size: 24px;
      margin-bottom: 8px;
    }
    
    .hero-category-item:hover {
      background-color: rgba(255,255,255,0.2);
      transform: translateY(-5px);
    }
    
    .scroll-down {
      position: absolute;
      bottom: 30px;
      left: 50%;
      transform: translateX(-50%);
    }
    
    .scroll-down a {
      color: white;
      font-size: 24px;
      width: 40px;
      height: 40px;
      line-height: 40px;
      text-align: center;
      display: block;
      border-radius: 50%;
      background-color: rgba(255,255,255,0.1);
      animation: bounce 2s infinite;
    }
    
    @keyframes bounce {
      0%, 20%, 50%, 80%, 100% { transform: translateY(0); }
      40% { transform: translateY(-10px); }
      60% { transform: translateY(-5px); }
    }
    
    .section-title h2 {
      font-weight: 700;
      position: relative;
      display: inline-block;
      margin-bottom: 15px;
    }
    
    .section-title h2:after {
      content: '';
      position: absolute;
      width: 60%;
      height: 3px;
      background-color: #2989d8;
      bottom: -10px;
      left: 50%;
      transform: translateX(-50%);
    }
    
    .about-bangalore img {
      border-radius: 15px;
    }
    
    .facts {
      display: flex;
      flex-direction: column;
      gap: 20px;
    }
    
    .fact-item {
      display: flex;
      align-items: flex-start;
    }
    
    .fact-item i {
      font-size: 24px;
      color: #2989d8;
      margin-right: 15px;
      margin-top: 5px;
    }
    
    .fact-item h5 {
      margin: 0 0 5px;
      font-weight: 600;
    }
    
    .fact-item p {
      margin: 0;
    }
    
    .testimonial-card {
      background-color: white;
      border-radius: 15px;
      overflow: hidden;
      box-shadow: 0 5px 15px rgba(0,0,0,0.1);
      height: 100%;
    }
    
    .testimonial-content {
      padding: 25px;
      position: relative;
    }
    
    .testimonial-content:before {
      content: '"';
      font-size: 5rem;
      position: absolute;
      top: 10px;
      left: 10px;
      color: rgba(0,0,0,0.05);
      font-family: serif;
    }
    
    .testimonial-content p {
      position: relative;
      z-index: 1;
    }
    
    .testimonial-author {
      display: flex;
      align-items: center;
      padding: 15px 25px;
      background-color: #f8f9fa;
    }
    
    .testimonial-author img {
      width: 50px;
      height: 50px;
      border-radius: 50%;
      object-fit: cover;
      margin-right: 15px;
    }
    
    .testimonial-author h5 {
      margin: 0 0 5px;
      font-weight: 600;
    }
    
    .testimonial-author p {
      margin: 0;
      font-size: 0.85rem;
      color: #6c757d;
    }
    
    .cta-section {
      background: linear-gradient(135deg, #1e5799 0%,#207cca 51%,#2989d8 100%);
    }
    
    .rounded-lg {
      border-radius: 15px;
    }
    
    .shadow-lg {
      box-shadow: 0 15px 30px rgba(0,0,0,0.2) !important;
    }
    
    @media (max-width: 768px) {
      .hero-section {
        height: auto;
        padding: 120px 0 100px;
      }
      
      .hero-categories {
        justify-content: center;
      }
    }
  `]
})
export class HomeComponent implements OnInit {
  topAttractions: Attraction[] = [];

  constructor(private attractionService: AttractionService) { }

  ngOnInit(): void {
    this.attractionService.getTopRatedAttractions(4)
      .subscribe(attractions => this.topAttractions = attractions);
  }
}
